import { Component } from "react";
import Popup from "reactjs-popup";
import "./index.css";

const monthsList = [
  "January",
  "February",
  "March",
  "April",
  "May",
  "June",
  "July",
  "August",
  "September",
  "October",
  "November",
  "December",
];

class Home extends Component {
  state = {
    pageNo: 1,
    titleSearch: "",
    descriptionSearch: "",
    priceSearch: "",
    selectValue: monthsList[2],
  };

  onFormSubmit = () => {};

  addTitleValue = (event) => {
    this.setState({ titleSearch: event.target.value });
  };

  addDescriptionValue = (event) => {
    this.setState({ descriptionSearch: event.target.value });
  };

  addPriceValue = (event) => {
    const inputValue = parseFloat(event.target.value);

    if (isNaN(inputValue) || inputValue <= 0) {
      alert("Please Enter a Positive Value");
    } else {
      this.setState({ priceSearch: inputValue });
    }
  };

  changeSelectItem = (event) => {
    this.setState({ selectValue: event.target.value });
  };

  loadNextPage = () => {
    this.setState((prevState) => ({
      pageNo: prevState.pageNo + 1,
    }));
  };

  loadPrevPage = () => {
    const { pageNo } = this.state;
    if (pageNo > 1) {
      this.setState((prevState) => ({
        pageNo: prevState.pageNo - 1,
      }));
    }
  };

  render() {
    const { selectValue } = this.state;
    return (
      <div className="mainContainer">
        <div className="titleContainer">
          <p>Transaction Dashboard</p>
        </div>
        <div className="dataContainer">
          <div className="responseChangeContainer">
            <Popup
              className="popupContainer"
              modal
              trigger={
                <button className="searchButton">Search Transaction</button>
              }
            >
              {(close) => (
                <div className="popupContentContainer">
                  <div className="closeContainer">
                    <button type="button" onClick={() => close()}>
                      x
                    </button>
                  </div>
                  <form className="form" onSubmit={this.onFormSubmit}>
                    <div className="labelContainer">
                      <label htmlFor="title"></label>
                      <input
                        type="text"
                        id="title"
                        onChange={this.addTitleValue}
                      />
                    </div>
                    <div className="labelContainer">
                      <label htmlFor="description"></label>
                      <input
                        type="text"
                        id="description"
                        onChange={this.addDescriptionValue}
                      />
                    </div>
                    <div className="labelContainer">
                      <label htmlFor="price"></label>
                      <input
                        type="text"
                        id="price"
                        onChange={this.addPriceValue}
                      />
                    </div>
                    <button type="submit" className="submitButton">
                      submit
                    </button>
                  </form>
                </div>
              )}
            </Popup>

            <select
              className="selectElement"
              onChange={this.changeSelectItem}
              value={selectValue}
            >
              {monthsList.map((item) => (
                <option key={item} value={item}>
                  {item}
                </option>
              ))}
            </select>
          </div>
          <table>
            <tbody>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Description</th>
                <th>Price</th>
                <th>Category</th>
                <th>Sold</th>
                <th>Image</th>
              </tr>
            </tbody>
          </table>
          <div className="responseChangeContainer">
            <p className="text">Page: {}</p>
            <>
              <button type="button" onClick={this.loadNextPage}>
                Next
              </button>
              <button type="button" onClick={this.loadPrevPage}>
                Previous
              </button>
            </>
            <p className="text">Per Page: {}</p>
          </div>
        </div>
        <div className="statsContainer">
          <h1>Statistics - {selectValue}(selected month name from dropdown)</h1>
          <div className="labelContainer">
            <p>Total Sale</p>
            <p></p>
          </div>
          <div className="labelContainer">
            <p>Total Sold Item</p>
            <p></p>
          </div>
          <div className="labelContainer">
            <p>Total not Sold Item</p>
            <p></p>
          </div>
        </div>
        <div className="statsContainer">
          <h1>
            Bar Chart Stats - {selectValue}(selected month name from dropdown)
          </h1>
        </div>
      </div>
    );
  }
}
