import { Component } from "react";
import axios from "axios";
import { BiSearch } from "react-icons/bi";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "./index.css";

const monthsList = [
  "January",
  "February",
  "March",
  "April",
  "May",
  "June",
  "July",
  "August",
  "September",
  "October",
  "November",
  "December",
];

const data = [
  {
    group_name: "0-100",
    no_of_items: 20,
  },
  {
    group_name: "100-200",
    no_of_items: 80,
  },
  {
    group_name: "200-300",
    no_of_items: 50,
  },
  {
    group_name: "300-400",
    no_of_items: 100,
  },
];

class Home extends Component {
  state = {
    pageNo: 1,
    search: "",
    selectValue: monthsList[2],
    tableDataList: [],
  };

  componentDidMount() {
    this.getRequiredData();
    this.getStatsData();
  }

  onFormSubmit = () => {
    this.getRequiredData();
  };

  getRequiredData = async () => {
    const { searchValue } = this.state;
    let month;
    switch (searchValue) {
      case monthsList[0]:
        month = 1;
        break;
      case monthsList[1]:
        month = 2;
        break;
      case monthsList[2]:
        month = 3;
        break;
      case monthsList[3]:
        month = 4;
        break;
      case monthsList[4]:
        month = 5;
        break;
      case monthsList[5]:
        month = 6;
        break;
      case monthsList[6]:
        month = 7;
        break;
      case monthsList[7]:
        month = 8;
        break;
      case monthsList[8]:
        month = 9;
        break;
      case monthsList[9]:
        month = 10;
        break;
      case monthsList[10]:
        month = 11;
        break;
      case monthsList[11]:
        month = 12;
        break;
      default:
        month = 3;
    }

    const { search, pageNo } = this.state;

    const apiUrl = `http://localhost:3001/home/?month=${month}&search=${search}&page_no=${pageNo}`;

    const options = {
      headers: {
        Accept: "application/json",
        "Access-Control-Allow-Origin": "http://localhost:3001/",
      },
    };
    console.log(options, axios);
    try {
      const responseData = await axios.get(apiUrl, options);
      this.setState({ tableDataList: responseData.data });
    } catch (error) {
      console.log(error.response);
    }
  };

  addSearchValue = (event) => {
    this.setState({ search: event.target.value });
  };

  changeSelectItem = (event) => {
    this.setState({ selectValue: event.target.value });
  };

  loadNextPage = () => {
    this.setState((prevState) => ({
      pageNo: prevState.pageNo + 1,
    }));
  };

  loadPrevPage = () => {
    const { pageNo } = this.state;
    if (pageNo > 1) {
      this.setState((prevState) => ({
        pageNo: prevState.pageNo - 1,
      }));
    }
  };

  getStatsData = async () => {
    const { month } = this.state;
    const apiUrl = `http://localhost:3001/home/response/?month=${month}`;
    console.log(apiUrl);

    const options = {
      headers: {
        Accept: "application/json",
        "Access-Control-Allow-Origin": apiUrl,
      },
    };
    console.log(options);

    try {
      const responseData = await axios.get(apiUrl, options);
    } catch (error) {
      console.log(error.response);
    }
  };

  render() {
    const { selectValue, pageNo } = this.state;
    return (
      <div className="mainContainer">
        <div className="titleContainer">
          <p className="title">Transaction Dashboard</p>
        </div>
        <div>
          <div className="responseChangeContainer">
            <form className="form" onSubmit={this.changeSelectItem}>
              <input
                type="text"
                id="title"
                className="inputElement"
                placeholder="Search transaction"
                onChange={this.addSearchValue}
              />
              <button type="submit" className="searchButton">
                <BiSearch className="searchIcon" />
              </button>
            </form>
            <select
              className="selectElement"
              onChange={this.changeSelectItem}
              value={selectValue}
            >
              {monthsList.map((item) => (
                <option key={item} value={item}>
                  {item}
                </option>
              ))}
            </select>
          </div>
          <div className="dataContainer">
            <table>
              <tbody>
                <tr className="tableRow">
                  <th className="tableHead">ID</th>
                  <th className="tableHead">Title</th>
                  <th className="tableHead">Description</th>
                  <th className="tableHead">Price</th>
                  <th className="tableHead">Category</th>
                  <th className="tableHead">Sold</th>
                  <th className="tableHead">Image</th>
                </tr>
              </tbody>
            </table>
          </div>
          <div className="responseChangeContainer">
            <p className="text">Page: {pageNo}</p>
            <>
              <button
                type="button"
                className="searchButton btn text"
                onClick={this.loadNextPage}
              >
                Next
              </button>
              <p>-</p>
              <button
                type="button"
                className="searchButton btn text"
                onClick={this.loadPrevPage}
              >
                Previous
              </button>
            </>
            <p className="text">Per Page: 10</p>
          </div>
        </div>

        <div className="statsContainer">
          <h1 className="head">
            Statistics - {selectValue}
            <span>(selected month name from dropdown)</span>
          </h1>
          <div className="statsDataContainer">
            <div className="labelContainer">
              <p className="statText">Total Sale </p>
              <p className="statText">{}</p>
            </div>
            <div className="labelContainer">
              <p className="statText">Total Sold Item </p>
              <p className="statText">{}</p>
            </div>
            <div className="labelContainer">
              <p className="statText">Total not Sold Item </p>
              <p className="statText">{}</p>
            </div>
          </div>
        </div>
        <div className="statsContainer">
          <h1 className="head">
            Bar Chart Stats - {selectValue}
            <span>(selected month name from dropdown)</span>
          </h1>
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={data} margin={{ top: 5 }}>
              <XAxis
                dataKey="group_name"
                tick={{ stroke: "gray", strokewidth: 1 }}
              />
              <YAxis tick={{ stroke: "gray", strokewidth: 0 }} />
              <Legend wrapperStyle={{ padding: 20 }} />
              <Bar
                dataKey="no_of_items"
                name="Price range"
                fill="#1f77b4"
                barSize="15%"
              />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    );
  }
}

export default Home;
