import { Component } from "react";
import axios from "axios";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Legend,
  ResponsiveContainer,
} from "recharts";
import "./index.css";

const monthsList = [
  "January",
  "February",
  "March",
  "April",
  "May",
  "June",
  "July",
  "August",
  "September",
  "October",
  "November",
  "December",
];

const data = [
  {
    group_name: "0-100",
    no_of_items: 20,
  },
  {
    group_name: "100-200",
    no_of_items: 80,
  },
  {
    group_name: "200-300",
    no_of_items: 50,
  },
  {
    group_name: "300-400",
    no_of_items: 100,
  },
];

class Home extends Component {
  state = {
    pageNo: 1,
    search: "",
    selectValue: monthsList[2],
  };

  componentDidMount() {
    this.getRequiredData();
    this.getStatsData();
  }

  onFormSubmit = () => {
    this.getRequiredData();
  };

  getRequiredData = async () => {
    const { searchValue } = this.state;
    let month;
    switch (searchValue) {
      case monthsList[0]:
        month = 1;
        break;
      case monthsList[1]:
        month = 2;
        break;
      case monthsList[2]:
        month = 3;
        break;
      case monthsList[3]:
        month = 4;
        break;
      case monthsList[4]:
        month = 5;
        break;
      case monthsList[5]:
        month = 6;
        break;
      case monthsList[6]:
        month = 7;
        break;
      case monthsList[7]:
        month = 8;
        break;
      case monthsList[8]:
        month = 9;
        break;
      case monthsList[9]:
        month = 10;
        break;
      case monthsList[10]:
        month = 11;
        break;
      case monthsList[11]:
        month = 12;
        break;
      default:
        month = 3;
    }

    const { search } = this.state;

    const apiUrl = ` http://localhost:3001/home/?month=${month}&search=${search}`;

    console.log(apiUrl);
    const options = {
      headers: {
        Accept: "application/json",
        "Access-Control-Allow-Origin": apiUrl,
      },
    };
    console.log(options);
    console.log(axios);

    try {
      console.log(1);
    } catch (error) {
      console.log(error.response);
    }
  };

  addSearchValue = (event) => {
    this.setState({ search: event.target.value });
  };

  changeSelectItem = (event) => {
    this.setState({ selectValue: event.target.value });
  };

  loadNextPage = () => {
    this.setState((prevState) => ({
      pageNo: prevState.pageNo + 1,
    }));
  };

  loadPrevPage = () => {
    const { pageNo } = this.state;
    if (pageNo > 1) {
      this.setState((prevState) => ({
        pageNo: prevState.pageNo - 1,
      }));
    }
  };

  getStatsData = async () => {
    const { month } = this.state;
    const apiUrl = `http://localhost:3001/home/response/?month=${month}`;
    console.log(apiUrl);

    const options = {
      headers: {
        Accept: "application/json",
        "Access-Control-Allow-Origin": apiUrl,
      },
    };
    console.log(options);

    try {
      console.log(1);
    } catch (error) {
      console.log(error.response);
    }
  };

  render() {
    const { selectValue } = this.state;
    return (
      <div className="mainContainer">
        <div className="titleContainer">
          <p>Transaction Dashboard</p>
        </div>
        <div className="dataContainer">
          <div className="responseChangeContainer">
            <form className="form" onSubmit={this.onFormSubmit}>
              <div className="labelContainer">
                <input type="text" id="title" onChange={this.addSearchValue} />
              </div>
            </form>
            <select
              className="selectElement"
              onChange={this.changeSelectItem}
              value={selectValue}
            >
              {monthsList.map((item) => (
                <option key={item} value={item}>
                  {item}
                </option>
              ))}
            </select>
          </div>
          <table>
            <tbody>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Description</th>
                <th>Price</th>
                <th>Category</th>
                <th>Sold</th>
                <th>Image</th>
              </tr>
            </tbody>
          </table>
          <div className="responseChangeContainer">
            <p className="text">Page: {}</p>
            <>
              <button type="button" onClick={this.loadNextPage}>
                Next
              </button>
              <button type="button" onClick={this.loadPrevPage}>
                Previous
              </button>
            </>
            <p className="text">Per Page: {}</p>
          </div>
        </div>
        <div className="statsContainer">
          <h1>Statistics - {selectValue}(selected month name from dropdown)</h1>
          <div className="labelContainer">
            <p>Total Sale</p>
            <p></p>
          </div>
          <div className="labelContainer">
            <p>Total Sold Item</p>
            <p></p>
          </div>
          <div className="labelContainer">
            <p>Total not Sold Item</p>
            <p></p>
          </div>
        </div>
        <div className="statsContainer">
          <h1>
            Bar Chart Stats - {selectValue}(selected month name from dropdown)
          </h1>
          <ResponsiveContainer width="100%" height={500}>
            <BarChart data={data} margin={{ top: 5 }}>
              <XAxis
                dataKey="group_name"
                tick={{ stroke: "gray", strokewidth: 1 }}
              />
              <YAxis tick={{ stroke: "gray", strokewidth: 0 }} />
              <Legend wrapperStyle={{ padding: 30 }} />
              <Bar
                dataKey="no_of_items"
                name="Price range"
                fill="#1f77b4"
                barSize="20%"
              />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    );
  }
}

export default Home;
